/*View structure for view affiliate_totals */

/*!50001 DROP TABLE IF EXISTS `affiliate_totals` */;
/*!50001 DROP VIEW IF EXISTS `affiliate_totals` */;

/*!50001 CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `affiliate_totals` AS select `orders`.`affiliate` AS `affiliate`,count(0) AS `count`,sum(`orders`.`total`) AS `total`,sum(`orders`.`commission`) AS `commission` from `orders` where (`orders`.`status` in ('shipped','refunded','refund')) group by `orders`.`affiliate` */;

/*View structure for view daily_orders */

